async function traerPersonajes(){const e=document.getElementById("characters");await fetch("https://rickandmortyapi.com/api/character").then((e=>e.json())).then((t=>{t.results.forEach((t=>{var a=document.createElement("div");a.innerHTML=`\n        <img src="${t.image}" alt="${t.name}">\n        <h3>${t.name}</h3>\n        <p>Especie: ${t.species}</p>\n        <p>Estado: ${t.status}</p>\n        <button onclick="agregarFavorito(${t.id})">❣️</button>\n        `,e.appendChild(a)}))})).catch((e=>{console.error("Error fetching characters:",e)}))}traerPersonajes();